        println("У Вас нет скидки")
        moneySpent
    } else if (moneySpent >= NO_DISCOUNT && moneySpent < FIRST_DISCOUNT) {
        val discount = onePercent(moneySpent)
        val toPay = moneySpent - discount
        println(
            """Ваша скидка - 1%!
            |Она составить: $discount
            |Сумма к оплате: $toPay
        """.trimMargin()
        )
        moneySpent
    } else if (moneySpent >= FIRST_DISCOUNT) {
        val discount = fivePercent(moneySpent)
        val toPay = moneySpent - discount
        println(
            """Ваша скидка - 5%! 
            |Она составить: $discount
            |Сумма к оплате: $toPay
        """.trimMargin()
        )
        moneySpent
    } else {
        null
    }
}

fun onePercent(onePercent: Int): Double {
    return onePercent * ONE_PERCENT
}

fun fivePercent(fivePercent: Int): Double {
    return fivePercent * FIVE_PERCENT
}
```

Now, the total amount to pay (`toPay`) is correctly calculated by subtracting the discount from the original `moneySpent` value.

